  await db.run(
    `UPDATE bugs SET ${fields.join(', ')}, updated_at = CURRENT_TIMESTAMP WHERE id = ?`,
    values
  );

  return getBugById(id);
}

export async function deleteBug(id) {
  const db = await getDb();
  const result = await db.run('DELETE FROM bugs WHERE id = ?', id);
  return result.changes > 0;
}

export async function getAllIdeas() {
  const db = await getDb();
